import React, { useRef, useEffect } from "react";

const ImageBox = ({ image_base64, boxesCoordinates }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    if (!image_base64 || !boxesCoordinates || boxesCoordinates.length === 0) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const image = new Image();
    image.src = `data:image/jpeg;base64,${image_base64}`;
    image.onload = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // make sure is clean 
      ctx.drawImage(image, 0, 0, canvas.width, canvas.height);

      // Draw boxes on the image based on the coordinates
      boxesCoordinates.forEach((box) => {
        ctx.strokeStyle = "008000"; // if can find it it is green 
        ctx.lineWidth = 2; // line width 
        ctx.strokeRect(box.x, box.y, box.width, box.height); // draw the box
      });
    };
  }, [image_base64, boxesCoordinates]);

  return <canvas ref={canvasRef} width={800} height={600} />;
};

export default ImageBox;
